import {_apply} from "../xeogl/xeogl.module.js"
import {Plugin} from "./Plugin.js";

/**
 Base class for Viewer plugins that load models.

 @class ModelsPlugin
 */
class ModelsPlugin extends Plugin {

    /**
     * @constructor
     *
     * @param {String} [id] ID for this plugin, so that we can find it within {@link Viewer#plugins}.
     * @param {Viewer} viewer The Viewer.
     * @param {Class} modelClass The JavaScript class for the type of model this plugin will manage.
     * @param {Object} cfg  Plugin configuration.
     */
    constructor(id, viewer, modelClass, cfg) {

        super(id, viewer, cfg);

        /**
         * @private
         */
        this._modelClass = modelClass;

        /**
         * <a href="http://xeogl.org/docs/classes/Model.html">xeogl.Models</a> currently loaded by this Plugin.
         * @type {{String:Model}}
         */
        this.models = {};

        /**
         * Saves load params for bookmarks.
         * @private
         */
        this._modelLoadParams = {};
    }

    /**
     * Loads a model into this Plugin's {@link Viewer}.
     *
     * @param params {*} Loading params.
     * @param params.id {String} ID to assign to the model, unique among all components in the Viewer's xeogl.Scene.
     * @returns {{xeogl.Model}} A <a href="http://xeogl.org/docs/classes/Model.html">xeogl.Model</a> representing the loaded model
     */
    load(params, ok) {
        const id = params.id;
        if (!id) {
            this.error("load() param expected: id");
            return;
        }
        if (this.viewer.scene.components[id]) {
            this.error(`Component with this ID already exists in viewer: ${id}`);
            return;
        }
        const model = new this._modelClass(this.viewer.scene, params);
        this._modelLoadParams[id] = _apply(params, {});
        this.models[id] = model;
        model.once("destroyed", () => {
            delete this.models[id];
            delete this._modelLoadParams[id];
        });
        if (ok) {
            model.once("loaded", ok);
        }
        return model;
    }

    /**
     * Unloads and destroys a model that was previously loaded by this Plugin.
     *
     * @param {String} id  ID of model to unload.
     */
    unload(id) {
        const model = this.models;
        if (!model) {
            this.error(`unload() model with this ID not found: ${id}`);
            return;
        }
        model.destroy();
    }

    /**
     * @private
     */
    send(name, value) {
        switch (name) {
            case "clear":
                this.clear();
                break;
        }
    }

    /**
     * @private
     */
    writeBookmark(bookmark) {
        bookmark[this.id] = this._modelLoadParams;
    }

    /**
     * @private
     */
    readBookmarkAsynch(bookmark, ok) {
        this.clear();
        var modelLoadParams = bookmark[this.id];
        if (modelLoadParams) {
            var modelParamsList = [];
            for (const id in modelLoadParams) {
                modelParamsList.push(modelLoadParams[id]);
            }
            if (modelParamsList.length === 0) {
                ok();
                return;
            }
            this._loadModel(modelParamsList, modelParamsList.length - 1, ok);
        }
    }

    _loadModel(modelLoadParams, i, ok) {
        this.load(modelLoadParams[i], function () {
            if (i === 0) {
                ok();
            } else {
                this._loadModel(modelLoadParams, i - 1, ok);
            }
        });
    }

    /**
     * Destroys models loaded by this plugin.
     */
    clear() {
        for (const id in this.models) {
            this.models[id].destroy();
        }
    }

    /**
     * Destroys this plugin, after first destroying any models it has loaded.
     */
    destroy() {
        this.clear();
        super.destroy();
    }
}

export {ModelsPlugin}

/**
 @desc Base class for {@link Viewer} plugin classes.
 */
class Plugin {

    /**
     * Creates this Plugin and installs it into the given {@link Viewer}.
     *
     * @param {string} id ID for this plugin, unique among all plugins in the viewer.
     * @param {Viewer} viewer The viewer.
     * @param {Object} [cfg] Options
     */
    constructor(id, viewer, cfg) {

        /**
         * ID for this Plugin, unique within its {@link Viewer}.
         *
         * @type {string}
         */
        this.id = (cfg && cfg.id) ? cfg.id : id;

        /**
         * The Viewer that contains this Plugin.
         *
         * @type {Viewer}
         */
        this.viewer = viewer;

        /**
         * Subscriptions to events fired on this Plugin.
         * @private
         */
        this._eventSubs = {};

        viewer.addPlugin(this);
    }

    /**
     Subscribes to an event fired at this Plugin.

     @param {String} event The event
     @param {Function} callback Callback fired on the event
     */
    on(event, callback) {
        let subs = this._eventSubs[event];
        if (!subs) {
            subs = [];
            this._eventSubs[event] = subs;
        }
        subs.push(callback);
    }

    /**
     Fires an event at this Plugin.

     @param {String} event The event type name
     @param {Object} value The event parameters
     */
    fire(event, value) {
        const subs = this._eventSubs[event];
        if (subs) {
            for (let i = 0, len = subs.length; i < len; i++) {
                subs[i](value);
            }
        }
    }

    /**
     * Logs a message to the JavaScript developer console, prefixed with the ID of this Plugin.
     *
     * @param {String} msg The error message
     */
    log(msg) {
        console.log(`[xeoviewer plugin ${this.id}]: ${msg}`);
    }

    /**
     * Logs a warning message to the JavaScript developer console, prefixed with the ID of this Plugin.
     *
     * @param {String} msg The error message
     */
    warn(msg) {
        console.warn(`[xeoviewer plugin ${this.id}]: ${msg}`);
    }

    /**
     * Logs an error message to the JavaScript developer console, prefixed with the ID of this Plugin.
     *
     * @param {String} msg The error message
     */
    error(msg) {
        console.error(`[xeoviewer plugin ${this.id}]: ${msg}`);
    }

    /**
     * Sends a message to this Plugin.
     *
     * @private
     */
    send(name, value) {
        //...
    }

    /**
     * Saves the state of this Plugin to a bookmark.
     *
     * @private
     */
    writeBookmark(bookmark) {
        //...
    }

    /**
     * Restores the state of this Plugin from a bookmark.
     *
     * @private
     */
    readBookmark(bookmark) {
        //...
    }

    /**
     * Destroys this Plugin and removes it from its {@link Viewer}.
     */
    destroy() {
        this.viewer.removePlugin(this);
    }
}

export {Plugin}

import {math, Scene, CameraFlightAnimation, CameraControl} from "../xeogl/xeogl.module.js";

/**
 * The WebGL-based 3D Viewer class at the heart of the xeokit SDK.
 *
 * A Viewer wraps a single <a href="http://xeogl.org/docs/classes/Scene.html">xeogl.Scene</a>.
 *
 * Add {@link Plugin}s to a Viewer to extend its functionality.
 *
 * Save and load the state of a Viewer as JSON with {@link Viewer#getBookmark} and {@link Viewer#setBookmark}. Installed
 * {@link Plugin} instances will also save and load their state to and from the JSON.
 *
 * @public
 */
class Viewer {

    /**
     * @constructor
     * @param {Object} cfg  Viewer configuration.
     * @param {String} [cfg.id] Optional ID for this Viewer, defaults to the ID of its <a href="http://xeogl.org/docs/classes/Scene.html">xeogl.Scene</a>, which xeogl automatically generates.
     */
    constructor(cfg) {

        /**
         * Metadata about this Viewer.
         *
         * @property meta
         * @type {{system: string}}
         */
        this.meta = {
            systemId: "xeoviewer.org",
            authoring_tool: "xeoviewer.org"
        };

        /**
         * Subscriptions to events sent with {@link fire}.
         * @private
         */
        this._eventSubs = {};

        /**
         * The Viewer's <a href="http://xeogl.org/docs/classes/Scene.html">xeogl.Scene</a>.
         *
         * @property scene
         * @type {xeogl.Scene}
         */
        this.scene = new Scene({
            canvas: cfg.canvasId,
            webgl2: false,
            contextAttr: {preserveDrawingBuffer: true},
            transparent: !!cfg.transparent,
            gammaInput: true,
            gammaOutput: true
        });

        /**
         * The Viewer's ID.
         * @property id
         *
         * @type {String|Number}
         */
        this.id = cfg.id || this.scene.id;


        /**
         * The Viewer's <a href="http://xeogl.org/docs/classes/CameraFlightAnimation.html">xeogl.CameraFlightAnimation</a>, which
         * is used to fly the <a href="http://xeogl.org/docs/classes/Scene.html">xeogl.Scene</a>'s <a href="http://xeogl.org/docs/classes/Camera.html">xeogl.Camera</a> to given targets.
         *
         * @property cameraFlight
         * @type {xeogl.CameraFlightAnimation}
         */
        this.cameraFlight = new CameraFlightAnimation(this.scene, {
            fitFOV: 45,
            duration: 0.1
        });

        /**
         * The Viewer's <a href="http://xeogl.org/docs/classes/CameraControl.html">xeogl.CameraControl</a>, which
         * controls the <a href="http://xeogl.org/docs/classes/Scene.html">xeogl.Scene</a>'s <a href="http://xeogl.org/docs/classes/Camera.html">xeogl.Camera</a> with mouse,  touch and keyboard input.
         *
         * @property cameraControl
         * @type {xeogl.CameraControl}
         */
        this.cameraControl = new CameraControl(this.scene, {});

        /**
         * {@link Plugin}s that have been installed into this Viewer, mapped to their IDs.
         *
         * @property plugins
         * @type {{string:Plugin}}
         */
        this.plugins = {};
    }

    /**
     * Subscribes to an event fired at this Viewer.
     *
     * @param {String} event The event
     * @param {Function} callback Callback fired on the event
     */
    on(event, callback) {
        let subs = this._eventSubs[event];
        if (!subs) {
            subs = [];
            this._eventSubs[event] = subs;
        }
        subs.push(callback);
    }

    /**
     * Fires an event at this Viewer.
     *
     * @param {String} event Event name
     * @param {Object} value Event parameters
     */
    fire(event, value) {
        const subs = this._eventSubs[event];
        if (subs) {
            for (let i = 0, len = subs.length; i < len; i++) {
                subs[i](value);
            }
        }
    }

    /**
     * Logs a message to the JavaScript developer console, prefixed with the ID of this Viewer.
     *
     * @param {String} msg The message
     */
    log(msg) {
        console.log(`[xeoviewer viewer ${this.id}]: ${msg}`);
    }

    /**
     * Logs an error message to the JavaScript developer console, prefixed with the ID of this Viewer.
     *
     * @param {String} msg The error message
     */
    error(msg) {
        console.error(`[xeoviewer viewer ${this.id}]: ${msg}`);
    }

    /**
     * Installs a Plugin.
     *
     * @private
     */
    addPlugin(plugin) {
        if (this.plugins[plugin.id]) {
            this.error(`Plugin with this ID already installed: ${plugin.id}`);
        }
        this.plugins[plugin.id] = plugin;
        this.log(`Installed plugin: ${plugin.id}`);
    }

    /**
     * Uninstalls a Plugin, clearing content from it first.
     *
     * @private
     */
    removePlugin(plugin) {
        const installedPlugin = this.plugins[plugin.id];
        if (!installedPlugin) {
            this.error(`Can't remove plugin - no plugin with this ID is installed: ${plugin.id}`);
            return;
        }
        if (!installedPlugin !== plugin) {
            this.error(`Can't remove plugin - a different plugin is installed with this ID: ${plugin.id}`);
            return;
        }
        if (installedPlugin.clear) {
            installedPlugin.clear();
        }
        delete this.plugins[id];
        this.log(`Removed plugin: ${plugin.id}`);
    }

    /**
     * Sends a message to installed Plugins.
     *
     * The message can optionally be accompanied by a value.
     * @private
     */
    sendToPlugins(name, value) {
        const plugins = this.plugins;
        for (const id in plugins) {
            if (plugins.hasOwnProperty(id)) {
                plugins[id].send(name, value);
            }
        }
    }

    /**
     * Clears content from this Viewer and all installed {@link Plugin}s.
     */
    clear() {
        this.sendToPlugins("clear");
    }

    /**
     * Resets viewing state.
     *
     * Sends a "resetView" message to each installed {@link Plugin}.
     */
    resetView() {
        this.sendToPlugins("resetView");

        // TODO
        // this.show();
        // this.hide("space");
        // this.hide("DEFAULT");
    }

    /**
     * Gets a JSON bookmark that captures the state of the Viewer and all installed {@link Plugin}s.
     *
     * @returns {*} The bookmark
     */
    getBookmark() {

        const vecToArray = math.vecToArray;
        const bookmark = {};
        let id;
        let model;
        let modelState;
        let position;
        let scale;
        let rotation;
        let object;
        let objectState;

        /*
         // Save object states that have non-default properties

         const objectStates = [];
         for (id in this.scene.entities) {
         if (this.scene.entities.hasOwnProperty(id)) {
         object = this.scene.entities[id];
         objectState = null;
         position = getPosition(object);
         if (position) {
         objectState = objectState || {id};
         objectState.position = position;
         }
         scale = getScale(object);
         if (scale) {
         objectState = objectState || {id};
         objectState.scale = scale;
         }
         rotation = getRotation(object);
         if (rotation) {
         objectState = objectState || {id};
         objectState.rotation = rotation;
         }
         if (!object.visible) {
         objectState = objectState || {id};
         objectState.visible = false;
         }
         if (object.outlined) {
         objectState = objectState || {id};
         objectState.outlined = true;
         }
         if (!object.clippable) {
         objectState = objectState || {id};
         objectState.clippable = false;
         }
         if (!object.pickable) {
         objectState = objectState || {id};
         objectState.pickable = false;
         }
         if (!object.pickable) {
         objectState = objectState || {id};
         objectState.pickable = false;
         }
         if (objectState) {
         objectStates.push(objectState);
         }
         }
         }
         if (objectStates.length > 0) {
         bookmark.objects = objectStates;
         }

         const camera = this.camera;

         bookmark.lookat = {
         eye: vecToArray(camera.eye),
         look: vecToArray(camera.look),
         up: vecToArray(camera.up),
         worldZUp: worldZUp === true
         };

         // Save other viewer properties that have non-default values

         if (camera.gimbalLock !== true) {
         bookmark.gimbalLock = camera.gimbalLock;
         }

         if (camera.projection !== "perspective") {
         bookmark.projection = camera.projection;
         }

         if (camera.perspective.near !== 0.1) {
         bookmark.perspectiveNear = camera.perspective.near;
         }

         if (camera.perspective.far !== 10000.0) {
         bookmark.perspectiveFar = camera.perspective.far;
         }

         if (camera.perspective.fov !== 60.0) {
         bookmark.perspectiveFOV = camera.perspective.fov;
         }

         if (camera.ortho.near !== 0.1) {
         bookmark.orthoNear = camera.ortho.near;
         }

         if (camera.ortho.far !== 10000.0) {
         bookmark.orthoFar = camera.ortho.far;
         }

         if (camera.ortho.scale !== 1.0) {
         bookmark.orthoScale = camera.ortho.scale;
         }

         bookmark.viewFitFOV = this.cameraFlight.fitFOV;
         bookmark.viewFitDuration = this.cameraFlight.duration;
         */
        // Save plugin states

        for (const pluginId in this.plugins) {
            if (this.plugins.hasOwnProperty(pluginId)) {
                const plugin = this.plugins[pluginId];
                if (plugin.writeBookmark) {
                    plugin.writeBookmark(bookmark);
                }
            }
        }

        return bookmark;
    }

    /**
     * Restores the Viewer and all installed {@link Plugin}s to the state captured in the given JSON bookmark.
     *
     * @param {*} bookmark
     */
    setBookmark(bookmark) {

        for (const pluginId in this.plugins) {
            if (this.plugins.hasOwnProperty(pluginId)) {
                const plugin = this.plugins[pluginId];
                if (plugin.readBookmark) {
                    plugin.readBookmark(bookmark);
                }
            }
        }
    }

    getSnapshot(params = {}, ok) {
        return this.scene.canvas.getSnapshot({
            width: params.width, // Defaults to size of canvas
            height: params.height,
            format: params.format || "png" // Options are "jpeg" (default), "png" and "bmp"
        }, ok);
    }

    destroy() {

    }
}

function getPosition(object) {
    const position = object.position;
    if (position[0] !== 0 || position[1] !== 0 || position[2] !== 0) {
        return vecToArray(position);
    }
}

function getScale(object) {
    const scale = object.scale;
    if (scale[0] !== 1 || scale[1] !== 1 || scale[2] !== 1) {
        return vecToArray(scale);
    }
}

function getRotation(object) {
    const rotation = object.rotation;
    if (rotation[0] !== 0 || rotation[1] !== 0 || rotation[2] !== 0) {
        return vecToArray(rotation);
    }
}

export {Viewer}

import {VectorTextGeometry} from "../../../xeogl/VectorTextGeometry/VectorTextGeometry.js"
import {Plugin} from "./../../../viewer/Plugin.js";
import {
    Scene,
    AmbientLight,
    DirLight,
    Mesh,
    CylinderGeometry,
    SphereGeometry,
    PhongMaterial,
    math
} from "../../../xeogl/xeogl.module.js"


/**
 * Viewer plugin that shows the axii of the World-space coordinate system.
 *
 * @class AxisGizmoPlugin
 */
class AxisGizmoPlugin extends Plugin {

    /**
     * @constructor
     * @param {Viewer} viewer The Viewer.
     * @param {Object} cfg  Plugin configuration.
     * @param {String} [cfg.id="AxisGizmo"] Optional ID for this plugin, so that we can find it within {@link Viewer#plugins}.
     * @param {Array} [cfg.size=[250,250]] Initial size in pixels.
     */
    constructor(viewer, cfg) {

        cfg = cfg || {};

        super("AxisGizmo", viewer, cfg);

        var camera = viewer.scene.camera;

        var size = cfg.size || [250, 250];

        var canvas = camera.scene.canvas;

        // Create canvas for this gizmo

        var canvasId = "xeogl-axisHelper-canvas-" + math.createUUID();
        var body = document.getElementsByTagName("body")[0];
        var div = document.createElement('div');
        var style = div.style;
        style.height = size[0] + "px";
        style.width = size[1] + "px";
        style.padding = "0";
        style.margin = "0";
        style.float = "left";
        style.left = "410px";
        style.bottom = "350px";
        style.position = "absolute";
        style["z-index"] = "1000000";
        // style["background-color"] = "rgba(0,0,0,0.3)";
        div.innerHTML += '<canvas id="' + canvasId + '" style="width: ' + size[0] + 'px; height: ' + size[1] + 'px; float: left; margin: 0; padding: 0;"></canvas>';
        body.appendChild(div);
        var helperCanvas = document.getElementById(canvasId);

        canvas.on("boundary",
            function (boundary) {
                style.left = boundary[0] + 10 + "px";
                style.bottom = (boundary[0] + 20) + "px";
            });

        // The scene containing this helper
        var scene = new Scene({
            canvas: helperCanvas,
            transparent: true
        });

        // Custom lights
        scene.clearLights();

        new AmbientLight(scene, {
            color: [0.45, 0.45, 0.5],
            intensity: 0.9
        });

        new DirLight(scene, {
            dir: [-0.5, 0.5, -0.6],
            color: [0.8, 0.8, 0.7],
            intensity: 1.0,
            space: "view"
        });

        new DirLight(scene, {
            dir: [0.5, -0.5, -0.6],
            color: [0.8, 0.8, 0.8],
            intensity: 1.0,
            space: "view"
        });

        // Rotate helper in synch with target camera

        var helperCamera = scene.camera;

        camera.on("matrix", function () {

            var eye = camera.eye;
            var look = camera.look;
            var up = camera.up;

            var eyeLook = math.mulVec3Scalar(math.normalizeVec3(math.subVec3(eye, look, [])), 22);

            helperCamera.look = [0, 0, 0];
            helperCamera.eye = eyeLook;
            helperCamera.up = up;
        });

        // ----------------- Components that are shared among more than one mesh ---------------

        var arrowHead = new CylinderGeometry(scene, {
            radiusTop: 0.01,
            radiusBottom: 0.6,
            height: 1.7,
            radialSegments: 20,
            heightSegments: 1,
            openEnded: false
        });

        var arrowShaft = new CylinderGeometry(scene, {
            radiusTop: 0.2,
            radiusBottom: 0.2,
            height: 4.5,
            radialSegments: 20,
            heightSegments: 1,
            openEnded: false
        });

        var axisMaterial = new PhongMaterial(scene, { // Red by convention
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var xAxisMaterial = new PhongMaterial(scene, { // Red by convention
            diffuse: [1, 0.3, 0.3],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var xAxisLabelMaterial = new PhongMaterial(scene, { // Red by convention
            emissive: [1, 0.3, 0.3],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var yAxisMaterial = new PhongMaterial(scene, { // Green by convention
            diffuse: [0.3, 1, 0.3],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var yAxisLabelMaterial = new PhongMaterial(scene, { // Green by convention
            emissive: [0.3, 1, 0.3],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });


        var zAxisMaterial = new PhongMaterial(scene, { // Blue by convention
            diffuse: [0.3, 0.3, 1],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var zAxisLabelMaterial = new PhongMaterial(scene, {
            emissive: [0.3, 0.3, 1],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });

        var ballMaterial = new PhongMaterial(scene, {
            diffuse: [0.5, 0.5, 0.5],
            ambient: [0.0, 0.0, 0.0],
            specular: [.6, .6, .3],
            shininess: 80,
            lineWidth: 2
        });


        // ----------------- Meshes ------------------------------

        var meshes = [

            // Sphere behind gnomon

            new Mesh(scene, {
                geometry: new SphereGeometry(scene, {
                    radius: 9.0,
                    heightSegments: 60,
                    widthSegments: 60
                }),
                material: new PhongMaterial(scene, {
                    diffuse: [0.0, 0.0, 0.0],
                    emissive: [0.1, 0.1, 0.1],
                    ambient: [0.1, 0.1, 0.2],
                    specular: [0, 0, 0],
                    alpha: 0.4,
                    alphaMode: "blend",
                    frontface: "cw"
                }),
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false
            }),

            // Ball at center of axis

            new Mesh(scene, {  // Arrow
                geometry: new SphereGeometry(scene, {
                    radius: 1.0
                }),
                material: ballMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false
            }),

            // X-axis arrow, shaft and label

            new Mesh(scene, {  // Arrow
                geometry: arrowHead,
                material: xAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [-5, 0, 0],
                rotation: [0, 0, 90]
            }),

            new Mesh(scene, {  // Shaft
                geometry: arrowShaft,
                material: xAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [-2, 0, 0],
                rotation: [0, 0, 90]
            }),

            new Mesh(scene, {  // Label
                geometry: new VectorTextGeometry(scene, {text: "X", size: 1.5}),
                material: xAxisLabelMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [-7, 0, 0],
                billboard: "spherical"
            }),

            // Y-axis arrow, shaft and label

            new Mesh(scene, {  // Arrow
                geometry: arrowHead,
                material: yAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 5, 0]
            }),

            new Mesh(scene, {  // Shaft
                geometry: arrowShaft,
                material: yAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 2, 0]
            }),

            new Mesh(scene, {  // Label
                geometry: new VectorTextGeometry(scene, {text: "Y", size: 1.5}),
                material: yAxisLabelMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 7, 0],
                billboard: "spherical"
            }),

            // Z-axis arrow, shaft and label

            new Mesh(scene, {  // Arrow
                geometry: arrowHead,
                material: zAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 0, 5],
                rotation: [90, 0, 0]
            }),

            new Mesh(scene, {  // Shaft
                geometry: arrowShaft,
                material: zAxisMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 0, 2],
                rotation: [90, 0, 0]
            }),

            new Mesh(scene, {  // Label
                geometry: new VectorTextGeometry(scene, {text: "Z", size: 1.5}),
                material: zAxisLabelMaterial,
                pickable: false,
                collidable: false,
                visible: cfg.visible !== false,
                position: [0, 0, 7],
                billboard: "spherical"
            })
        ];

        /** Shows or hides this helper
         *
         * @param visible
         */
        this.setVisible = function (visible) {
            for (var i = 0; i < meshes.length; i++) {
                meshes[i].visible = visible;
            }
        }
    }
}

