            mode: 0,
            nrObjectsRead: 0,
            nrObjects: 0
        };
        bimServerAPI.setBinaryDataListener(o.topicId, binaryDataListener);
        bimServerAPI.downloadViaWebsocket({
            longActionId: o.topicId,
            topicId: o.topicId
        });
