                var list = objectsWaitingForGeometryData[geometryDataId];
                if (!list) {
                    list = [];
                    objectsWaitingForGeometryData[geometryDataId] = list;
                }
                list.push(oid);
            }
        }

        currentState.nrObjectsRead++;

        updateProgress();
    }
