/**
 * Wraps zip.js to provide an in-memory ZIP archive representing the 3DXML file bundle.
 *
 * Allows us to pluck each file from it as XML and JSON.
 *
 * @constructor
 */
var ZIP = function () {

    var reader;
    var files = {};

    /**
     Loads this ZIP

     @param src
     @param ok
     @param error
     */
    this.load = function (src, ok, error) {
        var self = this;
        zip.createReader(new zip.HttpReader(src), function (reader) {
            reader.getEntries(function (entries) {
                if (entries.length > 0) {
                    for (var i = 0, len = entries.length; i < len; i++) {
                        var entry = entries[i];
                        files[entry.filename] = entry;
                    }
                }
                ok();
            });
        }, error);
    };

    /**
     Gets a file as XML and JSON from this ZIP
     @param src
     @param ok
     @param error
     */
    this.getFile = function (src, ok, error) {
        var entry = files[src];
        if (!entry) {
            var errMsg = "ZIP entry not found: " + src;
            console.error(errMsg);
            if (error) {
                error(errMsg);
            }
            return;
        }
        entry.getData(new zip.TextWriter(), function (text) {

            // Parse to XML
            var parser = new DOMParser();
            var xmlDoc = parser.parseFromString(text, "text/xml");

            // Parse to JSON
            var json = xmlToJSON(xmlDoc, {});

            ok(xmlDoc, json);
        });
    };

    function xmlToJSON(node, attributeRenamer) {
        if (node.nodeType === node.TEXT_NODE) {
            var v = node.nodeValue;
            if (v.match(/^\s+$/) === null) {
                return v;
            }
        } else if (node.nodeType === node.ELEMENT_NODE ||
            node.nodeType === node.DOCUMENT_NODE) {
            var json = {type: node.nodeName, children: []};
            if (node.nodeType === node.ELEMENT_NODE) {
                for (var j = 0; j < node.attributes.length; j++) {
                    var attribute = node.attributes[j];
                    var nm = attributeRenamer[attribute.nodeName] || attribute.nodeName;
                    json[nm] = attribute.nodeValue;
                }
            }
            for (var i = 0; i < node.childNodes.length; i++) {
                var item = node.childNodes[i];
                var j = xmlToJSON(item, attributeRenamer);
                if (j) json.children.push(j);
            }
            return json;
        }
    }

    /**
     Disposes of this ZIP
     */
    this.destroy = function () {
        reader.close(function () {
            // onclose callback
        });
    };
};

function loadZIP(src, ok, err) {
    var zip = new ZIP();
    zip.load(src, function () {
        ok(zip);
    }, function (errMsg) {
        err("Error loading ZIP archive: " + errMsg);
    })
}

function stripURN(str) {
    var subStr = "urn:3DXML:";
    return (str.indexOf(subStr) === 0) ? str.substring(subStr.length) : str;
}


function getIDFromURI(str) {
    var hashIdx = str.lastIndexOf("#");
    return hashIdx != -1 ? str.substring(hashIdx + 1) : str;
}

export {XML3DModel}

import {ModelsPlugin} from "./../../ModelsPlugin.js";
import {XML3DModel} from "./XML3DModel.js";


/**
 * A viewer plugin that loads models from [3DXML](https://en.wikipedia.org/wiki/3DXML).
 *
 * # Overview
 *
 * - Supports the 3DXML V4.2 Schema
 * - We've massaged the name to XML3DModelsPlugin because it's not permitted to start a ECMA6 class name with a numeral.
 *
 * @example
 * const viewer = new Viewer({
 *      canvasId: "myCanvas"
 * });
 *
 * const plugin = new XML3DModelsPlugin(viewer, {
 *    id: "xml3d",                                               // Default value
 *    workerScriptsPath : "../../src/plugins/XML3DModels/zipjs/" // Path to zip.js workers dir
 * });
 *
 * const model = viewer.plugins.xml3d.load({                     // Find plugin on Viewer using its ID
 *   id: "myModel",
 *   src: "./models/XML3D/3dpreview.3dxml",
 *   edges: true,                                                // Default is false
 *   scale: [1.0, 1.0, 1.0]                                      // Default
 * });
 *
 * @class XML3DModelsPlugin
 */
class XML3DModelsPlugin extends ModelsPlugin {

    /**
     * @constructor
     * @param {Viewer} viewer The Viewer.
     * @param {Object} cfg  Plugin configuration.
     * @param {String} [cfg.id="XML3DModels"] Optional ID for this plugin, so that we can find it within {@link Viewer#plugins}.
     * @param {String} cfg.workerScriptsPath Path to the directory that contains the
     * bundled [zip.js](https://gildas-lormeau.github.io/zip.js/) archive, which is a dependency of this plugin. This directory
     * contains the script that is used by zip.js to instantiate Web workers, which assist with unzipping the 3DXML, which is a ZIP archive.
     */
    constructor(viewer, cfg) {
        super("XML3DModels", viewer, XML3DModel, cfg);
        cfg = cfg || {};
        if (!cfg.workerScriptsPath) {
            this.error("Config expected: workerScriptsPath");
            return
        }
        this._workerScriptsPath = cfg.workerScriptsPath;
    }

    /**
     Loads a 3DXML model into the viewer.

     */
    load(params) {
        params.workerScriptsPath = this._workerScriptsPath;
        return super.load(params);
    }
}

export {XML3DModelsPlugin}

