    _setEdges(flags) {
        (flags & RENDER_FLAGS.EDGES) ? this._layer.numEdgesObjects++ : this._layer.numEdgesObjects--;
        this._layer.setFlags(this._portionId, flags);
    }

    _setClippable(flags) {
        this._layer.setFlags(this._portionId, flags);
    }

    _setCollidable(flags) {
        this._layer.setFlags(this._portionId, flags);
    }

    _setPickable(flags) {
        this._layer.setFlags(this._portionId, flags);
    }

    _setColor(rgba) {
